# In[12]:


# Determine the number of rows containing outliers
# 1. Seleccionar columnas numricas
numeric_cols = df.select_dtypes(include='number').columns

# 2. Crear una mscara booleana para marcar las filas con outliers
outlier_mask = pd.DataFrame(False, index=df.index, columns=numeric_cols)  # Inicialmente todo False

# 3. Aplicar el criterio IQR a cada columna numrica
for col in numeric_cols:
    Q1 = df[col].quantile(0.25)  # Primer cuartil
    Q3 = df[col].quantile(0.75)  # Tercer cuartil
    IQR = Q3 - Q1                # Rango intercuartlico
    
    # Definir lmites inferior y superior
    lower_bound = Q1 - 1.5 * IQR
    upper_bound = Q3 + 1.5 * IQR
    
    # Marcar los valores fuera de los lmites
    outlier_mask[col] = (df[col] < lower_bound) | (df[col] > upper_bound)

# 4. Determinar en qu filas hay al menos un outlier
rows_with_outliers = outlier_mask.any(axis=1)

# 5. Contar el nmero de filas con al menos un outlier
num_rows_with_outliers = rows_with_outliers.sum()

print(f"Nmero de filas que contienen al menos un outlier: {num_rows_with_outliers}")


